package com.taosdata.jdbc.cases;

import com.taosdata.jdbc.TSDBDriver;
import com.taosdata.jdbc.utils.SpecifyAddress;
import org.junit.AfterClass;
import org.junit.Assert;
import org.junit.BeforeClass;
import org.junit.Test;

import java.sql.*;
import java.util.Properties;

public class RawBlockBinaryLengthTest {

    static String host = "127.0.0.1";
    static String dbName = "binary_length_test";
    static Connection connection;
    static Statement statement;

    static String createSql = "CREATE TABLE `stb_sba_tem_bdr` (`ts` TIMESTAMP, `body` VARCHAR(65000))";
    static String insertSql = "insert into `stb_sba_tem_bdr` values (now, )";

    @Test
    public void nativeQuery() throws SQLException {
        ResultSet resultSet = statement.executeQuery("select body from `stb_sba_tem_bdr`");
        resultSet.next();
        String body = resultSet.getString("body");
        //System.out.println(body);
        Assert.assertTrue(body.length() > 0);
    }

    @Test
    public void websocketQuery() throws SQLException {
        try (Connection connection = DriverManager.getConnection("jdbc:TAOS-RS://" + host + ":6041/" + dbName + "?user=root&password=taosdata&batchfetch=true");
             Statement stmt = connection.createStatement();
             ResultSet resultSet = stmt.executeQuery("select body from `stb_sba_tem_bdr`");
        ) {
            resultSet.next();
            String body = resultSet.getString("body");
            //System.out.println(body);
            Assert.assertTrue(body.length() > 0);
        }

    }

    @BeforeClass
    public static void before() throws SQLException {
        String url = SpecifyAddress.getInstance().getJniUrl();
        if (url == null) {
            url = "jdbc:TAOS://" + host + ":6030/?user=root&password=taosdata";
        }
        Properties properties = new Properties();
        properties.setProperty(TSDBDriver.PROPERTY_KEY_LOCALE, "C");
        properties.setProperty(TSDBDriver.PROPERTY_KEY_CHARSET, "UTF-8");
        connection = DriverManager.getConnection(url, properties);
        statement = connection.createStatement();
        statement.executeUpdate("drop database if exists " + dbName);
        statement.executeUpdate("create database if not exists " + dbName);
        statement.executeUpdate("use " + dbName);
        statement.executeUpdate(createSql);
        statement.executeUpdate(insertSql);
    }

    @AfterClass
    public static void after() {
        try {
            if (statement != null) {
                statement.executeUpdate("drop database if exists " + dbName);
                statement.close();
            }
            if (connection != null) {
                connection.close();
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
    }
}
